import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterOutlet } from '@angular/router';

@Component({
  selector: 'app-customer',
  standalone: true,
  imports: [CommonModule, RouterOutlet],
  templateUrl: './app.component.html',
  styleUrl: './app.component.css'
})
export class AppComponent implements OnInit{
  title = 'customer';
  users: string[] = []

  ngOnInit(): void {
      window.addEventListener("@stake/super/add/user", (e: Event) => {
        const detail = (<CustomEvent>e).detail
        this.users.push(detail.id + " - " + detail.name)
      })
  }

}


<div>
  <h1>Stake Customer on!</h1>

  <ul>
    <li *ngFor="let item of users">
      <span style="background: linear-gradient(to bottom, #660066 0%, #ff99cc 100%)">{{item}}</span>
    </li>
  </ul>

</div>


