import { Component, Input } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterOutlet } from '@angular/router';
import { FormsModule } from '@angular/forms';
import {v4 as uuidv4} from 'uuid';


@Component({
  selector: 'app-super',
  standalone: true,
  imports: [CommonModule, RouterOutlet, FormsModule],
  templateUrl: './app.component.html',
  styleUrl: './app.component.css'
})
export class AppComponent {

  user = ''

  users: string[] = []

  addUser() {

    this.users.push(uuidv4() + " - " + this.user)

    window.dispatchEvent(new CustomEvent("@stake/super/add/user",{
      detail: {
        id: uuidv4(),
        name: this.user
      }
    }))
  }
}
<style>
  h1.title{
    background: linear-gradient(to right, #f32170, #08ff4e,#23cfcc, #eedd44); 
    -webkit-text-fill-color: transparent; 
    background-clip: text;
  }

  button.btn-grad {
    background-image: linear-gradient(to right, #FDFC47 0%, #24FE41  51%, #FDFC47  100%);
    margin: 10px;
    padding: 15px 45px;
    text-align: center;
    text-transform: uppercase;
    transition: 0.5s;
    background-size: 200% auto;
    color: white;            
    box-shadow: 0 0 20px #eee;
    border-radius: 10px;
    display: block;
  }

  .btn-grad:hover {
    background-position: right center; /* change the direction of the change here */
    color: #fff;
    text-decoration: none;
  }



  input{
    padding: 14px;
    float: left;
    margin-right: 15px;
    width: 300px;
  }

</style>

<div>
  <h1 class="title">Stake Super on!</h1>

  <input type="text" id="user" [(ngModel)]="user">

  <button type="button" (click)="addUser()" class="btn-grad">Add User</button>

</div>

<router-outlet></router-outlet>
